import { Component, Input, OnInit } from '@angular/core';
import { FaceSnap } from '../model/face-snap.model';
import {FaceSnapsService} from "../services/face-snaps.service";

@Component({
  selector: 'app-face-snap',
  templateUrl: './face-snap.component.html',
  styleUrls: ['./face-snap.component.scss']
})
export class FaceSnapComponent implements OnInit {

  /*
  Pour qu'une propri√©t√© puisse √™tre inject√©e depuis
   l'ext√©rieur d'un component, il faut lui ajouter le d√©corateur  @Input()
   */
  @Input() faceSnap! : FaceSnap;
  messageButtom! : string;

  constructor(private faceSnapService: FaceSnapsService) {}


  ngOnInit(): void {
    this.messageButtom = "Oh snaps";
  }

  onSnap(){
    if(this.messageButtom == "Oh snaps"){
      this.messageButtom = "Oups unSnaps";
      this.faceSnapService.snapFaceSnapById(this.faceSnap.id, 'snap');
    }
    else{
      this.faceSnapService.snapFaceSnapById(this.faceSnap.id, 'unsnap');
      this.messageButtom = "Oh snaps";
    }
  }
}
